import React from 'react';
import { Row, Col } from 'reactstrap';

const AuthorList = ({ authors }) => {
  return (
    <Row>
      {authors.map(author => (
        <Col xs={12} key={author.eid}>
          <Row>
            <Col xs={2}>
              {author.img && <img src={author.img} alt={author.name} style={{ height: '24px' }} />}
            </Col>
            <Col xs={6}>
              <h3>{author.title}</h3>
            </Col>
            <Col xs={4}>
              <p>{author.deathYear}</p>
            </Col>
          </Row>
        </Col>
      ))}
    </Row>
  );
};

export default AuthorList;
