import React from 'react';
import { Table } from 'reactstrap';

const AuthorList = ({ authors }) => {
  return (
    <Table>
      <thead>
        <tr>
          <th>Image</th>
          <th>Nom</th>
          <th>Date de décès</th>
        </tr>
      </thead>
      <tbody>
        {authors.map(author => (
          <tr key={author.eid}>
            <td>
              {author.img && <img src={author.img} alt={author.name} height="50px" />}
            </td>
            <td>{author.title}</td>
            <td>{author.deathYear}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};
export default AuthorList;
