import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AuthorList = (props) => {
    const [authors, setAuthors] = useState([]);

    const getAuthors = () => {
    axios.get(`http://localhost:5000/api/authors?name=${props.name}`)
    .then(response => {
    setAuthors(response.data);
    })
    .catch(error => {
    console.log(error);
    });
    };

    useEffect(() => {
        getAuthors();
        }, [props.name, getAuthors]);

return (
<div>
{authors.map(author => (
<div key={author.eid}>
{author.cartouche.img_url && (
<img src={author.cartouche.img_url} alt={author.cartouche.img_alt} />
)}
<h2>{author.title_dict.title}</h2>
<p>{author.description}</p>
</div>
))}
</div>
);
};

export default AuthorList;
