import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import AuthorList from './/components/AuthorList';

const App = () => {
  const [name, setName] = useState('');
  const [authors, setAuthors] = useState([]);

  const handleSubmit = event => {
    event.preventDefault();
    axios.get(`http://localhost:5000/api/authors?name=${name}`)
      .then(response => {
        setAuthors(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <Container>
      <Row className="justify-content-center mt-5">
        <Col xs={12} md={6}>
          <h1 className="text-center mb-3">Search for authors</h1>
          <p className="text-center mb-5">Enter a name to search for authors with that name</p>
          <Form onSubmit={handleSubmit}>
            <Form.Group>
              <Form.Control type="text" value={name} onChange={event => setName(event.target.value)} />
            </Form.Group>
            <Button type="submit" variant="primary">Search</Button>
          </Form>
          {authors.length > 0 && <AuthorList authors={authors} />}
        </Col>
      </Row>
    </Container>
  );
};

export default App;
