import React, { useState } from 'react';
import AuthorList from './/components/AuthorList';

const App = () => {
const [name, setName] = useState('');
const [showAuthorList, setShowAuthorList] = useState(false);


const handleChange = event => {
setName(event.target.value);
};

const handleSubmit = event => {
event.preventDefault();
setShowAuthorList(true);

};

return (
<div>
<h1>Auteurs de la BNF</h1>
<p>Entrez un nom d'auteur pour afficher la liste des auteurs correspondants :</p>
<form onSubmit={handleSubmit}>
<input type="text" value={name} onChange={(event) => setName(event.target.value)} />
<button type="submit">Submit</button>
</form>
{showAuthorList && <AuthorList name={name} />}
</div>
);
};

export default App;
