import React, { useState } from 'react';
import axios from 'axios';
import AuthorList from '.components/AuthorList';



function App() {
const [name, setName] = useState(''); // état pour le nom saisi par l'utilisateur
const [authors, setAuthors] = useState([]); // état pour la liste des auteurs

// Fonction de gestion de soumission de formulaire
const handleSubmit = (event) => {
event.preventDefault(); // empêche le rechargement de la page

// Envoie une requête GET au serveur node.js avec le nom en paramètre
axios.get(`/api/authors?name=${name}`)
  .then(response => {
    setAuthors(response.data); // met à jour la liste des auteurs
  })
  .catch(error => {
    console.log(error);
  });

}

return (
<div>
<h1>Titre de votre application</h1>
<p>Description de votre application</p>

  {/* Formulaire permettant à l'utilisateur de saisir un nom */}
  <form onSubmit={handleSubmit}>
    <label htmlFor="name">Nom :</label>
    <input type="text" id="name" value={name} onChange={event => setName(event.target.value)} />
    <button type="submit">Envoyer</button>
  </form>

  {/* Affiche la liste des auteurs sous forme de liste */}

  <AuthorList authors={authors} />




</div>
);
}

export default App;
